GET_V_OFFENDER_ALL_SCHEDULES {
SELECT 'X' FROM V_OFFENDER_ALL_SCHEDULES NS WHERE NS.OFFENDER_ID_DISPLAY = :offenderIdDisplay AND NS.EVENT_STATUS = :eventStatus AND NS.EVENT_CLASS = :eventClass AND NS.EVENT_TYPE = :eventType AND NS.EVENT_DATE = :eventDate AND NS.START_TIME = :startTime AND NS.EVENT_ID != NVL (:eventId, 0)
}   