
OUMAGYRG_FIND_AGENCYLOCATIONTYPERG {
 	SELECT REF_CODE.CODE CODE , REF_CODE.DESCRIPTION DESCRIPTION FROM REFERENCE_CODES REF_CODE WHERE DOMAIN = 'AGY_LOC_TYPE' AND ((ACTIVE_FLAG = 'Y' AND EXPIRED_DATE IS NULL ) OR '' = 'ENTER-QUERY' ) ORDER BY LIST_SEQ , CODE , DESCRIPTION
}

OUMAGYRG_FIND_GEOGRAPHICREGIONRG {
 	SELECT REF_CODE.CODE CODE        ,REF_CODE.DESCRIPTION  DESCRIPTION FROM   REFERENCE_CODES REF_CODE WHERE   DOMAIN = 'GEOGRAPHIC'  AND    ((ACTIVE_FLAG = 'Y'  AND    EXPIRED_DATE IS NULL ) OR '' = 'ENTER-QUERY' ) ORDER BY LIST_SEQ , CODE , DESCRIPTION
}

OUMAGYRG_FIND_SUBAREARG {
 	SELECT AREA_CODE CODE , DESCRIPTION FROM AREAS WHERE AREA_TYPE = :AGENCYLOCATIONTYPE AND AREA_CLASS = 'SUB_AREA' AND PARENT_AREA_CODE = :AREACODE AND ((ACTIVE_FLAG = 'Y' AND EXPIRY_DATE IS NULL ) OR '' = 'ENTER-QUERY' ) ORDER BY LIST_SEQ, CODE , DESCRIPTION 
}
OUMAGYRG_FIND_SUBAREARG_TOT {
 	SELECT DISTINCT PARENT_AREA_CODE CODE  FROM AREAS WHERE  AREA_CLASS = 'SUB_AREA'  AND ((ACTIVE_FLAG = 'Y' AND EXPIRY_DATE IS NULL ) OR '' = 'ENTER-QUERY' )}


OUMAGYRG_FIND_AREARG {
 	SELECT  area_code CODE, DESCRIPTION FROM areas WHERE area_type = :AGENCYLOCATIONTYPE AND area_class = 'AREA' AND parent_area_code = :NOMSREGIONCODE
    AND ( ( active_flag = 'Y'  AND expiry_date IS NULL ) OR '' = 'ENTER-QUERY' )  ORDER by  LIST_SEQ, CODE , DESCRIPTION 
}

