HDC_BOARD_DECISIONS_T1_HDC_BOARD_DECISIONS{
SELECT DECISION_SEQ,HDC_REQUEST_REFERRAL_ID,COMMENT_TEXT,BOARD_ATTENDEES,CHAIR_STAFF_ID,BOARD_RECOMMENDATION,ATTACH_COMMENT_FLAG,SUCCESSFUL_ROTL_FLAG,OFFENDING_BEHAVIOUR_FLAG,HOME_CIRCUMSTANCES_FLAG,MEDICAL_OFFICER_COMMENT_FLAG,PROBATION_COMMENT_FLAG,THROUGHCARE_TEAM_COMMENT_FLAG,WING_OFFICER_COMMENT_FLAG,LIKELY_TO_FAIL_CURFEW_FLAG,REOFFENDING_PROBABILITY_FLAG,NO_SUITABLE_ACCOMMODATION_FLAG,THREAT_TO_PUBLIC_SAFETY_FLAG,OTHER_FLAG,CREATE_DATETIME,CREATE_USER_ID,MODIFY_DATETIME,MODIFY_USER_ID,MESSAGE_ID,SEAL_FLAG FROM HDC_BOARD_DECISIONS WHERE HDC_REQUEST_REFERRAL_ID=:hdcRequestReferralId AND DECISION_SEQ=:decisionSeq
}
HDC_BOARD_DECISIONS_T1_L_NUMBER{
SELECT 1 FROM hdc_request_referrals WHERE hdc_request_referral_id = :hdcRequestReferralId
}
HDC_BOARD_DECISIONS_T1_HDC_REQUEST_REFERRALS_UPDATE{
UPDATE hdc_request_referrals SET received_date = :createDatetime WHERE hdc_request_referral_id = :hdcRequestReferralId
}